import * as React from "react"
import { Divider, Paper, TextField, Checkbox, FormControlLabel } from "@mui/material"
import useLocalStorage from "../utils/localStorage"
export default function HoursSelection({ callback }) {
    const [breakfastVal, setBreakFastVal] = React.useState(undefined)
    const [dinerVal, setDinerVal] = React.useState(undefined)
    const [disabled, setDisabled] = React.useState(false)

    React.useEffect(() => {
        console.log(dinerVal, localStorage.getItem("hours"))
        if (dinerVal && breakfastVal) {
            callback({
                diner: dinerVal,
                breakfast: breakfastVal
            })
        }
    }, [breakfastVal, dinerVal])

    React.useEffect(() => {
        if (localStorage.getItem("hours").diner !== dinerVal) setDinerVal(localStorage.getItem("hours").diner)
        if (localStorage.getItem("hours").breakfast !== dinerVal) setBreakFastVal(localStorage.getItem("hours").breakfast)
    }, [localStorage.getItem("hours")])

    return (<Paper elevation={12} sx={{padding: 1}}>
        <FormControlLabel label="Compter les repas d'aujourd'ui" control={<Checkbox onChange={e => {
            if (!e.target.checked) {
                setDisabled(true)
            }
        }} checked={false} />}/>

        <TextField 
            type="time"
            label="Heure du déjeuner"
            InputLabelProps={{
                shrink: true
            }}
            inputProps={{
                step: 300
            }}
            value={breakfastVal}
            onChange={(e) => setBreakFastVal(e.target.value)}
            sx={{width: 150, display: "inline-flex"}}
        />
        <Divider orientation="vertical" flexItem sx={{marginLeft: 1, marginRight: 1, display: "inline-flex"}} />
        <TextField 
            type="time"
            label="Heure du dîner"
            value={dinerVal}
            InputLabelProps={{
                shrink: true
            }}
            inputProps={{
                step: 300
            }}
            onChange={(e) => setDinerVal(e.target.value)}
            sx={{width: 150, display: "inline-flex"}}
        />
    </Paper>)
}
