import * as React from "react"
import { Paper } from "@mui/material"
import MenuSelect from "./MenuSelection"
import MoneyInput from "./MoneyInput"

export default function DataSelection() {
    const [currentMenu, changeMenu] = React.useState('5*')
    const [currentMoney, changeCurrentMoney] = React.useState(0)

    const handleMenuChange = (e) => {
        changeMenu(e.target.value)
    }

    
    const handleMoneyChange = (e) => {
        changeCurrentMoney(e.target.value)
    }

    return (<Paper elevation={12} sx={{ padding: 1 }}>
        <MenuSelect handleChange={handleMenuChange}/>
        <MoneyInput handleChange={handleMoneyChange}/>
    </Paper>)
}
