export function getDaysTillEndOfMonth(type) {
    let dateToday = new Date();
    let lastDayOfMonth = getLastDayOfMonth();
    let daysUntilEndOfMonth = lastDayOfMonth - (dateToday.getDate() + 1);
    return daysUntilEndOfMonth
}

function getLastDayOfMonth() {
    let dateToday = new Date()
    return  new Date(dateToday.getFullYear(), dateToday.getMonth() + 1, 0).getDate()
}
