import * as React from "react"
import { Divider, Paper, TextField, Checkbox, FormControlLabel } from "@mui/material"
import useLocalStorage from "../utils/localStorage"
export default function HoursSelection({ callback }) {
    const [breakfastVal, setBreakFastVal] = useLocalStorage("breakfastHour", "12:15")
    const [dinerVal, setDinerVal] = useLocalStorage("dinerHour", "18:15")
    const [disabled, setDisabled] = React.useState(false)

    React.useEffect(() => {
        callback({
            diner: dinerVal,
            breakfast: breakfastVal
        })
    }, [breakfastVal, dinerVal, callback])

    React.useEffect(() => {
        if (localStorage.getItem("hours").breakfast !== "" || localStorage.getItem("hours").diner !== "") {
            setDisabled(false)
        } else {
            setDisabled(true)
        }
    }, [])

    return (<Paper elevation={12} sx={{padding: 1}}>
        <FormControlLabel label="Compter les repas d'aujourd'ui" control={<Checkbox onChange={e => {
            if (e.target.checked) {

            }
        }} checked={Boolean(localStorage.getItem("hours").diner && localStorage.getItem("hours").breakfast) ?? false} />}/>

        <TextField 
            type="time"
            label="Heure du déjeuner"
            InputLabelProps={{
                shrink: true
            }}
            inputProps={{
                step: 300
            }}
            value={breakfastVal}
            onChange={(e) => setBreakFastVal(e.target.value)}
            sx={{width: 150, display: "inline-flex"}}
        />
        <Divider orientation="vertical" flexItem sx={{marginLeft: 1, marginRight: 1, display: "inline-flex"}} />
        <TextField 
            type="time"
            label="Heure du dîner"
            value={dinerVal}
            InputLabelProps={{
                shrink: true
            }}
            inputProps={{
                step: 300
            }}
            onChange={(e) => setDinerVal(e.target.value)}
            sx={{width: 150, display: "inline-flex"}}
        />
    </Paper>)
}
