export function getDaysTillEndOfMonth(type, includesVacations) {
    let dateToday = new Date()
    let endDate = new Date(dateToday.getFullYear(), date.getMonth() + 1, 0)
    let total = 0
    if (includesVacations) {
        while (dateToday <= endDate) {
            if (dateToday.getDate() !== 0 && dateToday.getDate() !== 6) {
                if (type == "week") total += (includesVacations ? 1 : checkForVacation(dateToday))
            } else if (type == "we") {
                total += (includesVacations ? 1 : checkForVacation(dateToday))
            }
            dateToday.setDate(dateToday.getDate() + 1)
        }
    }
    return total
}

