import * as React from "react"
import DataSelection from "./DataSelection/DataSelection";
import DataDisplay from "./DataDisplay/DataDisplay";
import Calendar from "./CalendarDisplay";
import { createTheme, ThemeProvider, Divider } from '@mui/material';

function getDejPrice(menu) {
  switch(menu) {
    case '5/7':
      return 2.14
    case '5/7*':
      return 2.25
    case '7/7':
      return 1.87
    default :
      return 2.43
  }
}

function getMealPrice(menu) {
  switch(menu) {
    case '5/7':
      return 4.23
    case '5/7*':
      return 4.47
    case '7/7':
      return 3.74
    default :
      return 4.81
  }
}

function App() {
  const darktheme = createTheme({
    palette: {
      mode: "dark",
    }
  })

  const [menu, setMenu] = React.useState('5*')
  const [money, setMoney] = React.useState(0)
  const [meals, setMeals] = React.useState([0, 0])

  const [mealPrices, setMealPrices] = React.useState([])

  const [total, setTotal] = React.useState({
    price: 0,
    number: 0
  })

  React.useEffect(() => {
    let dej = {
      label: "Petit déjeuners",
      number: meals[0],
      price: meals[0] * getDejPrice(menu)
    }

    let midi = {
      label: "Déjeuners/Dîners",
      number: meals[1],
      price: meals[1] * getMealPrice(menu)
    }

    setMealPrices([dej, midi])

    let total = {
      price: midi.price + dej.price,
      number: midi.number + dej.number
    }

    setTotal(total)
  }, [menu, money, meals])

  return (
    <ThemeProvider theme={darktheme}>
      <DataSelection menuChange={setMenu} moneyChange={setMoney} mealsChange={setMeals}/>
      <Divider sx={{margin: 1}} />
      <DataDisplay mealPrices={mealPrices} total={total}/>
      <Divider sx={{margin: 1}} />
      <Calendar />
    </ThemeProvider>

    );
}

export default App;
