import * as React from "react"
import { Divider, Paper, TextField } from "@mui/material"
import useLocalStorage from "../utils/localStorage"
export default function HoursSelection() {
    const [breakfastVal, setBreakFastVal] = useLocalStorage("breakfastHour", "18:15")

    const handleChange = (e) => {
        console.log(e.target)
    }

    return (<Paper elevation={12} sx={{padding: 1}}>
        <TextField 
            type="time"
            label="Heure du déjeuner"
            defaultValue="12:15"
            InputLabelProps={{
                shrink: true
            }}
            inputProps={{
                step: 300
            }}
            value={breakfastVal}
            onChange={(e) => setBreakFastVal(e.target.value)}
            sx={{width: 150, display: "inline-flex"}}
        />
        <Divider orientation="vertical" flexItem sx={{marginLeft: 1, marginRight: 1, display: "inline-flex"}} />
        <TextField 
            type="time"
            label="Heure du dîner"
            defaultValue="18:15"
            InputLabelProps={{
                shrink: true
            }}
            inputProps={{
                step: 300
            }}
            onChange={handleChange}
            sx={{width: 150, display: "inline-flex"}}
        />
    </Paper>)
}
